client/src/contexts/modal-context.tsx.content:
import { createContext, useContext, useState } from "react";

export type ModalType =
  | "airlock"
  | "seeding"
  | "harvest"
  | "x-ray"
  | "field-journal"
  | "referral-beacon"
  | null;

interface ModalContextType {
  activeModal: ModalType;
  openModal: (modal: ModalType) => void;
  closeModal: () => void;
  modalData: Record<string, unknown>;
  setModalData: (data: Record<string, unknown>) => void;
}

const ModalContext = createContext<ModalContextType | undefined>(undefined);

export function ModalProvider({ children }: { children: React.ReactNode }) {
  const [activeModal, setActiveModal] = useState<ModalType>(null);
  const [modalData, setModalData] = useState<Record<string, unknown>>({});

  const openModal = (modal: ModalType) => {
    setActiveModal(modal);
  };

  const closeModal = () => {
    setActiveModal(null);
    setModalData({});
  };

  return (
    <ModalContext.Provider
      value={{ activeModal, openModal, closeModal, modalData, setModalData }}
    >
      {children}
    </ModalContext.Provider>
  );
}

export function useModal() {
  const context = useContext(ModalContext);
  if (!context) {
    throw new Error("useModal must be used within ModalProvider");
  }
  return context;
}

