@layer base {
  body {
    @apply font-sans antialiased bg-background text-foreground;
  }
}

/* Hide scrollbar utility */
.scrollbar-hide {
  -ms-overflow-style: none;
  scrollbar-width: none;
}
.scrollbar-hide::-webkit-scrollbar {
  display: none;
}

/* Micro Animations */
@keyframes rotate-slow {
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(360deg);
  }
}

@keyframes rotate-reverse {
  from {
    transform: rotate(360deg);
  }
  to {
    transform: rotate(0deg);
  }
}

@keyframes float {
  0%, 100% {
    transform: translateY(0px);
  }
  50% {
    transform: translateY(-12px);
  }
}

@keyframes pulse-glow {
  0%, 100% {
    opacity: 1;
    transform: scale(1);
  }
  50% {
    opacity: 0.8;
    transform: scale(1.05);
  }
}

@keyframes pulse-slow {
  0%, 100% {
    opacity: 1;
  }
  50% {
    opacity: 0.6;
  }
}

@keyframes node-scatter {
  0% {
    transform: translate(0, 0) scale(1);
    opacity: 1;
  }
  100% {
    transform: translate(var(--scatter-tx), var(--scatter-ty)) scale(0);
    opacity: 0;
  }
}

@keyframes backdrop-fade {
  0% {
    opacity: 0;
  }
  100% {
    opacity: 1;
  }
}

@keyframes modal-materialize {
  0% {
    opacity: 0;
    transform: scale(0.85) translateY(20px);
  }
  100% {
    opacity: 1;
    transform: scale(1) translateY(0);
  }
}

@keyframes fade-in {
  0% {
    opacity: 0;
  }
  100% {
    opacity: 1;
  }
}

@keyframes glow-pulse {
  0%, 100% {
    opacity: 0.8;
    box-shadow: 0 0 10px rgba(0, 82, 255, 0.5);
  }
  50% {
    opacity: 1;
    box-shadow: 0 0 20px rgba(0, 82, 255, 0.8);
  }
}

@keyframes scan-line {
  0% {
    background-position: 0 0;
  }
  100% {
    background-position: 100% 0;
  }
}

@keyframes glitch {
  0%, 100% {
    opacity: 1;
  }
  50% {
    opacity: 0.7;
  }
}

@keyframes spin-slow {
  0% {
    transform: rotate(0deg);
  }
  100% {
    transform: rotate(360deg);
  }
}

/* Dashboard Animations */
@keyframes wave {
  0%, 100% {
    transform: translateX(0) scaleY(1);
  }
  50% {
    transform: translateX(-25%) scaleY(1.1);
  }
}

@keyframes droplet {
  0% {
    transform: translateY(0) scaleY(1);
    opacity: 1;
  }
  100% {
    transform: translateY(400px) scaleY(2);
    opacity: 0;
  }
}

@keyframes pulse-glow {
  0%, 100% {
    opacity: 0.8;
    filter: brightness(1);
  }
  50% {
    opacity: 1;
    filter: brightness(1.3);
  }
}

@keyframes surge {
  0%, 100% {
    opacity: 0;
  }
  50% {
    opacity: 1;
  }
}

@keyframes surge-line {
  0% {
    stroke-dashoffset: 100;
    stroke-dasharray: 100;
  }
  100% {
    stroke-dashoffset: 0;
    stroke-dasharray: 100;
  }
}

@keyframes scan-right {
  0% {
    transform: translateX(0);
  }
  100% {
    transform: translateX(calc(100vw - 2px));
  }
}

@keyframes glitch-line {
  0%, 100% {
    transform: translateX(0);
    filter: none;
  }
  25% {
    transform: translateX(-2px);
    filter: hue-rotate(90deg);
  }
  50% {
    transform: translateX(2px);
    filter: hue-rotate(-90deg);
  }
  75% {
    transform: translateX(-1px);
    filter: hue-rotate(45deg);
  }
}

@keyframes grow-branch {
  from {
    stroke-dashoffset: 100;
    stroke-dasharray: 100;
  }
  to {
    stroke-dashoffset: 0;
    stroke-dasharray: 100;
  }
}

@keyframes typing {
  from {
    width: 0;
  }
  to {
    width: 100%;
  }
}

@keyframes shimmer {
  0% {
    transform: translateX(-100%);
  }
  100% {
    transform: translateX(100%);
  }
}

/* Digital Rain Background */
.digital-rain {
  position: absolute;
  inset: 0;
  background: 
    linear-gradient(transparent 0%, rgba(0, 82, 255, 0.03) 50%, transparent 100%),
    repeating-linear-gradient(
      0deg,
      transparent,
      transparent 2px,
      rgba(0, 82, 255, 0.05) 2px,
      rgba(0, 82, 255, 0.05) 4px
    );
  animation: rain-fall 20s linear infinite;
  opacity: 0.3;
}

@keyframes rain-fall {
  0% {
    background-position: 0 0;
  }
  100% {
    background-position: 0 100vh;
  }
}

/* Storm Lightning Effect */
.storm-lightning {
  position: absolute;
  inset: 0;
  background: radial-gradient(circle at 30% 20%, rgba(0, 82, 255, 0.3) 0%, transparent 50%);
  animation: lightning-flash 2s ease-in-out infinite;
}

@keyframes lightning-flash {
  0%, 90%, 100% {
    opacity: 0;
  }
  92%, 96% {
    opacity: 1;
  }
  94%, 98% {
    opacity: 0;
  }
}

@layer utilities {
  .animate-backdrop-fade {
    animation: backdrop-fade 0.3s ease-out forwards;
  }

  .animate-modal-materialize {
    animation: modal-materialize 0.4s cubic-bezier(0.34, 1.56, 0.64, 1) forwards;
  }

  .animate-fade-in {
    animation: fade-in 0.2s ease-out forwards;
  }

  .animate-glow-pulse {
    animation: glow-pulse 2s ease-in-out infinite;
  }

  .scan-line {
    animation: scan-line 1s linear infinite;
    background-size: 200% 100%;
  }

  .animate-glitch {
    animation: glitch 0.3s ease-in-out;
  }

  .animate-spin-slow {
    animation: spin-slow 8s linear infinite;
  }

  .glow-button {
    box-shadow: 0 0 20px rgba(0, 82, 255, 0.5);
    transition: all 0.3s ease;
  }

  .glow-button:hover {
    box-shadow: 0 0 30px rgba(0, 82, 255, 0.8);
  }

  .animate-star-burst {
    --tx: calc((Math.random() - 0.5) * 200px);
    --ty: calc((Math.random() - 0.5) * 200px);
  }
  .animate-rotate-slow {
    animation: rotate-slow 20s linear infinite;
  }

  .animate-rotate-reverse {
    animation: rotate-reverse 25s linear infinite;
  }

  .animate-rotate-slow-reverse {
    animation: rotate-slow 30s linear infinite reverse;
  }

  .animate-float {
    animation: float 4s ease-in-out infinite;
  }

  .animate-pulse-glow {
    animation: pulse-glow 3s ease-in-out infinite;
  }

  .animate-pulse-slow {
    animation: pulse-slow 2s ease-in-out infinite;
  }

  .perspective {
    perspective: 1000px;
  }

  /* Dashboard Animations */
  .animate-wave {
    animation: wave 3s ease-in-out infinite;
  }

  .animate-droplet {
    animation: droplet 1.5s ease-in forwards;
  }

  .animate-surge {
    animation: surge 0.5s ease-out;
  }

  .animate-surge-line {
    animation: surge-line 0.5s ease-out;
  }

  .animate-scan-right {
    animation: scan-right 4s linear infinite;
  }

  .animate-glitch-line {
    animation: glitch-line 0.3s ease-in-out infinite;
  }

  .animate-grow-branch {
    animation: grow-branch 1s ease-out forwards;
  }

  .animate-typing {
    animation: typing 1.5s steps(40) forwards;
    overflow: hidden;
    white-space: nowrap;
  }

  .animate-shimmer {
    animation: shimmer 3s ease-in-out infinite;
  }
}

/**
 * Using the elevate system.
 * Automatic contrast adjustment.
 *
 * <element className="hover-elevate" />
 * <element className="active-elevate-2" />
 *
 * // Using the tailwind utility when a data attribute is "on"
 * <element className="toggle-elevate data-[state=on]:toggle-elevated" />
 * // Or manually controlling the toggle state
 * <element className="toggle-elevate toggle-elevated" />
 *
 * Elevation systems have to handle many states.
 * - not-hovered, vs. hovered vs. active  (three mutually exclusive states)
 * - toggled or not
 * - focused or not (this is not handled with these utilities)
 *
 * Even without handling focused or not, this is six possible combinations that
 * need to be distinguished from eachother visually.
 */
@layer utilities {

  /* Hide ugly search cancel button in Chrome until we can style it properly */
  input[type="search"]::-webkit-search-cancel-button {
    @apply hidden;
  }

  /* Placeholder styling for contentEditable div */
  [contenteditable][data-placeholder]:empty::before {
    content: attr(data-placeholder);
    color: hsl(var(--muted-foreground));
    pointer-events: none;
  }

  /* .no-default-hover-elevate/no-default-active-elevate is an escape hatch so consumers of
   * buttons/badges can remove the automatic brightness adjustment on interactions
   * and program their own. */
  .no-default-hover-elevate {}

  .no-default-active-elevate {}


  /**
   * Toggleable backgrounds go behind the content. Hoverable/active goes on top.
   * This way they can stack/compound. Both will overlap the parent's borders!
   * So borders will be automatically adjusted both on toggle, and hover/active,
   * and they will be compounded.
   */
  .toggle-elevate::before,
  .toggle-elevate-2::before {
    content: "";
    pointer-events: none;
    position: absolute;
    inset: 0px;
    /*border-radius: inherit;   match rounded corners */
    border-radius: inherit;
    z-index: -1;
    /* sits behind content but above backdrop */
  }

  .toggle-elevate.toggle-elevated::before {
    background-color: var(--elevate-2);
  }

  /* If there's a 1px border, adjust the inset so that it covers that parent's border */
  .border.toggle-elevate::before {
    inset: -1px;
  }

  /* Does not work on elements with overflow:hidden! */
  .hover-elevate:not(.no-default-hover-elevate),
  .active-elevate:not(.no-default-active-elevate),
  .hover-elevate-2:not(.no-default-hover-elevate),
  .active-elevate-2:not(.no-default-active-elevate) {
    position: relative;
    z-index: 0;
  }

  .hover-elevate:not(.no-default-hover-elevate)::after,
  .active-elevate:not(.no-default-active-elevate)::after,
  .hover-elevate-2:not(.no-default-hover-elevate)::after,
  .active-elevate-2:not(.no-default-active-elevate)::after {
    content: "";
    pointer-events: none;
    position: absolute;
    inset: 0px;
    /*border-radius: inherit;   match rounded corners */
    border-radius: inherit;
    z-index: 999;
    /* sits in front of content */
  }

  .hover-elevate:hover:not(.no-default-hover-elevate)::after,
  .active-elevate:active:not(.no-default-active-elevate)::after {
    background-color: var(--elevate-1);
  }

  .hover-elevate-2:hover:not(.no-default-hover-elevate)::after,
  .active-elevate-2:active:not(.no-default-active-elevate)::after {
    background-color: var(--elevate-2);
  }

  /* If there's a 1px border, adjust the inset so that it covers that parent's border */
  .border.hover-elevate:not(.no-hover-interaction-elevate)::after,
  .border.active-elevate:not(.no-active-interaction-elevate)::after,
  .border.hover-elevate-2:not(.no-hover-interaction-elevate)::after,
  .border.active-elevate-2:not(.no-active-interaction-elevate)::after,
  .border.hover-elevate:not(.no-hover-interaction-elevate)::after {
    inset: -1px;
  }
}

