  async getTokenSaleData(): Promise<TokenSaleData> {
    return this.tokenSaleData;
  }

  async getStakingMultipliers(): Promise<StakingMultiplier[]> {
    return this.stakingMultipliers;
  }
}

export const storage = new MemStorage();

