import { useState } from "react";
import { AlertTriangle, TrendingUp, ExternalLink, RefreshCw } from "lucide-react";
import { ModalContainer } from "./modal-container";
import { useModal } from "@/contexts/modal-context";

interface HarvestModalProps {
  isMature: boolean;
}

type WithdrawalState = "preview" | "processing" | "success";

export function HarvestModal({ isMature = true }: HarvestModalProps) {
  const { closeModal } = useModal();
  const [slideProgress, setSlideProgress] = useState(0);
  const [withdrawalState, setWithdrawalState] = useState<WithdrawalState>("preview");
  const [shouldReinvest, setShouldReinvest] = useState(false);

  // Mock data
  const principal = 1000;
  const yieldGenerated = 234.56;
  const bonusPoints = 500;
  const totalReturn = principal + yieldGenerated;
  const roi = ((yieldGenerated / principal) * 100).toFixed(2);
  const daysStaked = 45;
  const estimatedGas = 0.08;
  const estimatedAPY = 18.5;

  const handleSlideComplete = () => {
    if (slideProgress === 100) {
      setWithdrawalState("processing");

      // Simulate blockchain transaction
      setTimeout(() => {
        setWithdrawalState("success");
        setTimeout(closeModal, 3000);
      }, 2500);
    }
  };

  // Success State
  if (withdrawalState === "success") {
    return (
      <ModalContainer onClose={closeModal} title="HARVEST">
        <div className="flex flex-col items-center justify-center py-12 space-y-6">
          <div className="w-20 h-20 rounded-full bg-green-500/20 border-2 border-green-500 flex items-center justify-center">
            <svg className="w-10 h-10 text-green-500" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
            </svg>
          </div>

          <h3 className="text-2xl font-bold text-green-400">HARVEST COMPLETE</h3>

          <div className="text-center space-y-2">
            <p className="text-blue-300/70">
              ${totalReturn.toLocaleString()} transferred to your wallet
            </p>
            <div className="text-sm text-blue-400/60 font-mono flex items-center gap-2 justify-center">
              Tx: 0xAbC...789
              <ExternalLink className="w-3 h-3" />
            </div>
          </div>

          {shouldReinvest && (
            <div className="p-3 rounded border border-green-500/20 bg-green-500/5 text-center">
              <p className="text-sm text-green-400">
                 Yield automatically reinvested
              </p>
            </div>
          )}
        </div>
      </ModalContainer>
    );
  }

  // Processing State
  if (withdrawalState === "processing") {
    return (
      <ModalContainer onClose={closeModal} title="HARVEST">
