import { useState, useEffect } from "react";
import { GlassPanel } from "./glass-panel";

interface SonarProps {
  isBooting: boolean;
}

interface BotActivity {
  id: number;
  type: string;
  action: string;
  gas: number;
  angle: number;
}

export function Sonar({ isBooting }: SonarProps) {
  const [rotation, setRotation] = useState(0);
  const [activities, setActivities] = useState<BotActivity[]>([]);
  const [currentActivity, setCurrentActivity] = useState<string>("");

  // Rotating sweep
  useEffect(() => {
    if (isBooting) return;

    const interval = setInterval(() => {
      setRotation((prev) => (prev + 2) % 360);
    }, 16); // 120 RPM = 2 degrees per frame at 60fps

    return () => clearInterval(interval);
  }, [isBooting]);

  // Simulate bot detections
  useEffect(() => {
    if (isBooting) return;

    const interval = setInterval(() => {
      const newActivity: BotActivity = {
        id: Date.now(),
        type: Math.random() > 0.5 ? "HARVESTER" : "REBALANCER",
        action: "Executed",
        gas: parseFloat((Math.random() * 0.05 + 0.01).toFixed(2)),
        angle: Math.random() * 360,
      };

      setActivities((prev) => [...prev, newActivity].slice(-3));
      setCurrentActivity(`> [${newActivity.type}] ${newActivity.action}. Gas: $${newActivity.gas}`);

      // Clear typing after 3 seconds
      setTimeout(() => setCurrentActivity(""), 3000);
    }, 8000);

    return () => clearInterval(interval);
  }, [isBooting]);

  return (
    <GlassPanel className="h-[280px]">
      <div className="flex flex-col h-full">
        {/* Title */}
        <h3 className="text-sm font-medium text-primary/70 tracking-wider mb-4">
          SONAR
        </h3>

        {/* Radar Circle */}
        <div className="flex-1 flex items-center justify-center">
          <div className="relative w-40 h-40">
            {/* Radar background */}
            <svg className="absolute inset-0" viewBox="0 0 100 100">
              {/* Concentric circles */}
              <circle
                cx="50"
                cy="50"
                r="45"
                fill="none"
                stroke="hsl(var(--primary) / 0.2)"
                strokeWidth="0.5"
              />
              <circle
                cx="50"
                cy="50"
                r="30"
                fill="none"
                stroke="hsl(var(--primary) / 0.2)"
                strokeWidth="0.5"
              />
              <circle
                cx="50"
                cy="50"
                r="15"
                fill="none"
                stroke="hsl(var(--primary) / 0.2)"
                strokeWidth="0.5"
              />

              {/* Cross hairs */}
              <line
                x1="50"
                y1="5"
                x2="50"
                y2="95"
                stroke="hsl(var(--primary) / 0.2)"
                strokeWidth="0.5"
              />
              <line
                x1="5"
                y1="50"
                x2="95"
                y2="50"
                stroke="hsl(var(--primary) / 0.2)"
                strokeWidth="0.5"
              />

              {/* Sweep line */}
              <line
                x1="50"
                y1="50"
                x2="50"
                y2="5"
                stroke="hsl(var(--primary))"
                strokeWidth="2"
                transform={`rotate(${rotation} 50 50)`}
                style={{ transformOrigin: "50% 50%" }}
              />

              {/* Sweep gradient */}
              <defs>
                <radialGradient id="sweep-gradient">
                  <stop offset="0%" stopColor="hsl(var(--primary))" stopOpacity="0.5" />
                  <stop offset="100%" stopColor="hsl(var(--primary))" stopOpacity="0" />
                </radialGradient>
              </defs>
              <path
                d={`M 50 50 L 50 5 A 45 45 0 0 1 ${50 + 45 * Math.sin((rotation * Math.PI) / 180)} ${50 - 45 * Math.cos((rotation * Math.PI) / 180)} Z`}
                fill="url(#sweep-gradient)"
                opacity="0.5"
              />
            </svg>

            {/* Bot activity blips */}
            {activities.map((activity) => {
              const x = 50 + 30 * Math.cos((activity.angle * Math.PI) / 180);
              const y = 50 + 30 * Math.sin((activity.angle * Math.PI) / 180);
              return (
                <div
                  key={activity.id}
                  className="absolute w-2 h-2 bg-primary rounded-full animate-ping"
                  style={{
                    left: `${x}%`,
                    top: `${y}%`,
                    transform: "translate(-50%, -50%)",
                  }}
                />
              );
            })}
          </div>
        </div>

        {/* Activity Feed */}
        <div className="mt-4 h-12 overflow-hidden">
          {currentActivity && (
            <div className="font-mono text-xs text-green-500 dark:text-green-400 animate-typing">
              {currentActivity}
            </div>
          )}
        </div>
      </div>
    </GlassPanel>
  );
}

